import React from "react";
import {
  TouchableOpacity,
  Text,
  View,
  StyleSheet,
  Image,
  Dimensions,
  ScrollView
} from "react-native";
import { Header } from "react-native-elements";
import { AuthContext } from "../App";
import jwt_decode from "jwt-decode";

import {
  ContainerScroll,
  Container,
  ContainerHeader,
  ContainerItemStory,
  ContainerPhoto,
  Photo,
  Name,
  PostPhoto,
  ContainerActions,
  ContainerActionsIcons,
  GroupIcons,
  Label
} from "../Components/Posts/styles";
import { FontAwesome5 } from "@expo/vector-icons";
import Icon from "react-native-vector-icons/Ionicons";

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

export const ProfileContext = React.createContext();

const Profile = ({ navigation, route }) => {
  const { state, dispatch } = React.useContext(AuthContext);
  let url = state.url;
  const token = state.token;
  const decoded = jwt_decode(token);
  const user_id = decoded;

  const scrollRef = React.useRef();

  const [iconsConfigure] = React.useState({
    color: "#333",
    size: 20,
    style: {
      paddingRight: 15
    }
  });

  const [loading, controlLoading] = React.useState(true);
  const [max, setMax] = React.useState(0);

  const [myposts, fetchPosts] = React.useState([]);
  const [myprofile, fetchProfile] = React.useState([]);

  /*const fetch_user = () => {
    fetch(`${url}/code_reservoir/my_profile.php/?max=${max}`, {
      method: "GET",
      "Content-Type": "application/json",
      headers: {
        Authorization: `Bearer ${state.token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        fetchProfile(data.my_profile);
        fetchPosts(data.my_posts);
        controlLoading(false);
        setMax(data.profile_count);
        //dispatch({ type: "FETCH_PROFILE", payload: data.my_profile });
      })
      .catch(err => console.log(state.token));
    setTimeout(fetch_user, 6000);
  };*/

  const scroll_to_Top = () => {
    scrollRef.current.scrollTo({
      y: 0,
      animated: true
    });
  };

  let data_sent = JSON.stringify({
    user_id: user_id,
    max: max
  });

  const fetch_user = () => {
    fetch(`${url}/code_reservoir/single_profile.php/?data=${data_sent}`, {
      method: "GET",
      "Content-Type": "application/json",
      headers: {
        Authorization: `Bearer ${state.token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        fetchProfile(data.my_profile);
        fetchPosts(data.my_posts);
        controlLoading(false);
        setMax(data.profile_count);
      })
      .catch(err => console.log(err));
    setTimeout(fetch_user, 6000);
  };

  React.useEffect(() => {
    fetch_user();
  }, []);

  const like = async id => {
    const newPost = myposts.map(p =>
      p.p_id === id
        ? { ...p, post_liker: user_id, total_likes: p.total_likes + 1 }
        : p
    );

    fetchPosts(newPost);

    const data = new FormData();
    data.append("post_id", id);
    data.append("token", state.token);

    fetch(`${url}/code_reservoir/like_post.php`, {
      method: "POST",
      body: data
      //headers: {
      //  'Content-Type': 'multipart/form-data'
      //}
    })
      .then(res => res.json())
      .then(data => console.log(data.message));
    //.catch(err=>console.log(err))
  };

  const unlike = async (e, id) => {
    const newPost = myposts.map(p =>
      p.p_id === id
        ? { ...p, post_liker: null, total_likes: p.total_likes - 1 }
        : p
    );

    fetchPosts(newPost);
    const data = new FormData();
    data.append("post_id", id);
    data.append("token", state.token);

    fetch(`${url}/code_reservoir/unlike_post.php`, {
      method: "POST",
      body: data
      //headers: {
      //  'Content-Type': 'multipart/form-data'
      //}
    }).then(res => res.json());
    // .then(data=>alert(data.message))
    // .catch(err=>console.log(err))
  };

  return (
    <View style={{ flex: 1, backgroundColor: "#fff" }}>
      {myprofile.map(user => (
        <Header
          placement="left"
          centerComponent={
            <Text
              onPress={() => navigation.goBack()}
              s
              style={{
                fontSize: 20,
                fontWeight: "bold",
                color: "black",
                marginLeft: -5
              }}
            >
              {user.full_name}
            </Text>
          }
          rightComponent={
            <TouchableOpacity
              onPress={() =>
                navigation.navigate("EditProfile", {
                  user_id: user_id
                })
              }
            >
              <Icon name="pencil" size={25} color="rgb(95, 32, 155)" />
            </TouchableOpacity>
          }
          containerStyle={{
            backgroundColor: "#fff",
            //justifyContent: 'space-around',
            height: "13%"
          }}
        />
      ))}
      {loading ? (
        <View
          style={{
            justifyContent: "center",
            alignItems: "center",
            marginTop: HEIGHT / 3
          }}
        >
          <Image
            source={require("../Images/loader5.gif")}
            style={{ height: 100, width: 100 }}
          />
        </View>
      ) : (
        <ScrollView ref={scrollRef}>
          <ContainerScroll>
            {myprofile.map(p => (
              <View>
                <Image
                  source={{ uri: `${url}/code_reservoir/${p.coverphoto}` }}
                  style={styles.coverPhoto}
                />
                <View style={{ alignItems: "center" }}>
                  <Image
                    source={{ uri: `${url}/code_reservoir/${p.user_img}` }}
                    style={styles.avartar}
                  />
                  <Text style={styles.name}>{p.full_name}</Text>
                  <Text style={styles.bio}>{p.bio}</Text>
                  <TouchableOpacity
                    style={styles.editProfile}
                    onPress={() =>
                      navigation.navigate("EditProfile", {
                        user_id: p.user_id,
                        full_name: p.full_name,
                        bio: p.bio,
                        coverphoto: p.coverphoto,
                        user_img: p.user_img
                      })
                    }
                  >
                    <Text style={styles.editProfileText}>Edit Profile</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ))}

            <View style={{ marginTop: 100 }}>
              {myposts.map((post, index) => (
                <Container key={index}>
                  <ContainerHeader>
                    <TouchableOpacity onPress={() => scroll_to_Top()}>
                      <ContainerItemStory>
                        <ContainerPhoto>
                          <Photo
                            source={{
                              uri: `${url}/code_reservoir/${post.user_img}`
                            }}
                          />
                        </ContainerPhoto>
                        <Name>{post.full_name}</Name>
                      </ContainerItemStory>
                    </TouchableOpacity>
                    <FontAwesome5 name="ellipsis-h" size={14} color="#888" />
                  </ContainerHeader>
                  <PostPhoto
                    source={{ uri: `${url}/code_reservoir/${post.post_media}` }}
                  />
                  <ContainerActions>
                    <ContainerActionsIcons>
                      <GroupIcons>
                        {post.post_liker === null ? (
                          <TouchableOpacity onPress={e => like(post.p_id)}>
                            <Icon
                              name="heart-outline"
                              size={25}
                              color="black"
                            />
                          </TouchableOpacity>
                        ) : (
                          <TouchableOpacity onPress={e => unlike(e, post.p_id)}>
                            <Icon name="heart" color="red" size={25} />
                          </TouchableOpacity>
                        )}
                        <TouchableOpacity
                          style={{ marginLeft: 10, marginTop: 2 }}
                          onPress={() =>
                            navigation.navigate("SinglePost", {
                              post_id: post.p_id
                            })
                          }
                        >
                          <FontAwesome5 name="comment" {...iconsConfigure} />
                        </TouchableOpacity>
                      </GroupIcons>
                      <FontAwesome5 name="bookmark" {...iconsConfigure} />
                    </ContainerActionsIcons>
                    <Label>{post.total_likes} Likes</Label>
                    <Label>{post.post_caption}</Label>
                    {post.total_comments > 0 ? (
                      <TouchableOpacity>
                        <Text
                          onPress={() =>
                            navigation.navigate("SinglePost", {
                              post_id: post.p_id
                            })
                          }
                          style={{ color: "grey" }}
                        >
                          {post.total_comments} comments
                        </Text>
                      </TouchableOpacity>
                    ) : null}
                  </ContainerActions>
                </Container>
              ))}
            </View>
          </ContainerScroll>
        </ScrollView>
      )}
    </View>
  );
};

