import Icon from "react-native-vector-icons/Ionicons";
import React from "react";
import {
  Image,
  TextInput,
  Text,
  View,
  StyleSheet,
  ScrollView,
  Dimensions,
  TouchableOpacity
} from "react-native";
import HeaderButtons from "react-navigation-header-buttons";
import { Header } from "react-native-elements";
import { Video, AVPlaybackStatus } from "expo-av";
import { AuthContext } from "../App";

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

import jwt_decode from "jwt-decode";

const PostVideo = ({ route, navigation }) => {
  const MyContext = React.createContext(MyContext);
  const { state, dispatch } = React.useContext(AuthContext);
  let url = state.url;
  const initialState = {
    post_caption: "",
    post_media: ""
  };
  const [mystate, setState] = React.useState(initialState);
  const [loading, controlLoading] = React.useState(false);

  const token = state.token;
  const decoded = jwt_decode(token);
  const user_id = decoded;
  const video = React.useRef(null);
  const [status, setStatus] = React.useState({});
  const make_post = () => {
    controlLoading(true);

    let uri = route.params.video;
    let uriParts = route.params.video.split(".");
    let filename = uri.split("/").pop();
    let fileType = uriParts[uriParts.length - 1];
    const data = new FormData();
    data.append("post_media", {
      uri,
      name: `${filename}`,
      type: `video/${fileType}`
    });
    data.append("post_caption", mystate.post_caption);
    data.append("token", state.token);

    fetch(`${url}/code_reservoir/post_video.php`, {
      method: "POST",
      body: data,
      headers: {
        "Content-Type": "multipart/form-data"
      }
    })
      .then(res => res.json())
      .then(data => {
        controlLoading(false);
        //alert(data.message);
        navigation.navigate("Home");
      })
      .catch(err => {
        controlLoading(false);
        alert(err);
      });
  };

  return (
    <View>
      {loading ? (
        <View style={styles.modalWrapper}>
          <Image
            source={require("../Images/loader5.gif")}
            style={{ height: 100, width: 100 }}
          />
        </View>
      ) : null}
      <Header
        placement="left"
        leftComponent={
          <Icon
            name="close-outline"
            size={35}
            color="black"
            onPress={() => navigation.goBack()}
          />
        }
        rightComponent={
          <TouchableOpacity onPress={() => make_post()}>
            <Icon name="checkmark-circle-outline" size={35} color="black" />
          </TouchableOpacity>
        }
        containerStyle={{
          backgroundColor: "#fff",
          //justifyContent: 'space-around',
          height: 100
        }}
      />
      <ScrollView>
        <View>
          <View>
            <TextInput
              multiline
              placeholder="Write a Caption . . ."
              style={styles.input}
              onChangeText={post_caption => {
                setState(prevState => ({ ...prevState, post_caption }));
              }}
              value={mystate.post_caption}
            />
          </View>
          <Video
            ref={video}
            source={{ uri: `${route.params.video}` }}
            style={styles.myVideo}
            useNativeControls
            resizeMode="cover"
            shouldPlay
            isLooping
            onPlaybackStatusUpdate={status => setStatus(() => status)}
          />
          <View style={styles.postContainer}></View>
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  myVideo: {
    height: 400,
    width: WIDTH,
    borderBottomLeftRadius: 10,
    borderBottomRightRadius: 10,
    marginTop: 10
  },
  input: {
    marginTop: 100,
    paddingTop: 20,
    fontSize: 20,
    paddingLeft: 10
  },

  myHeader: {
    width: "100%",
    paddingTop: 40,
    paddingBottom: 30,
    backgroundColor: "#fff"
  },

  postContainer: {
    display: "flex",
    alignItems: "center"
  },
  postButton: {
    backgroundColor: "#fff",
    borderWidth: 1,
    borderRadius: 10,
    width: 70,
    height: 30,
    borderColor: "#333",
    color: "#fff",
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
  },

  modalWrapper: {
    height: HEIGHT,
    backgroundColor: "#fff",
    justifyContent: "center",
    alignItems: "center"
  }
});

export default PostVideo;
