import React from "react";
import {
  TouchableOpacity,
  Text,
  View,
  Dimensions,
  Image,
  StyleSheet,
  TextInput
} from "react-native";
import { List, ListItem, Header } from "react-native-elements";
import { Avatar } from "react-native-elements/dist/avatar/Avatar";
import { ScrollView, RectButton } from "react-native-gesture-handler";
import { AuthContext } from "../App";
import Icon from "react-native-vector-icons/Ionicons";

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

const ChatSection = ({ navigation }) => {
  const [chats, setChats] = React.useState([]);
  const [loading, controlLoading] = React.useState(true);
  const [formVisible, setFormVisible] = React.useState(false);
  const [filteredData, setFilteredData] = React.useState([]);
  const [search, setSearch] = React.useState("");
  const { state, dispatch } = React.useContext(AuthContext);
  let url = state.url;

  const showForm = () => {
    setFormVisible(true);
  };

  const fetch_chats = () => {
    fetch(`${url}/code_reservoir/fetch_chats.php`, {
      methods: "GET",
      "Content-Type": "application/json",
      headers: {
        Authorization: `Bearer ${state.token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        setFilteredData(data);
        setChats(data);
        controlLoading(false);
      })
      .catch(err => console.log(err));
    setTimeout(fetch_chats, 6000);
  };

  const searchFilterFunction = text => {
    if (text) {
      const newData = chats.filter(function(item) {
        const itemData = item.full_name
          ? item.full_name.toUpperCase()
          : "".toUpperCase();
        const textData = text.toUpperCase();
        return itemData.indexOf(textData) > -1;
      });
      setFilteredData(newData);
      setSearch(text);
    } else {
      setFilteredData(users);
      setSearch(text);
    }
  };

  React.useEffect(() => {
    fetch_chats();
  }, []);

  return (
    <View style={{ flex: 1, backgroundColor: "white" }}>
      <Header
        placement="left"
        leftComponent={
          !formVisible ? (
            <Text style={{ fontSize: 20, fontWeight: "bold", color: "black" }}>
              Find Users
            </Text>
          ) : null
        }
        centerComponent={
          formVisible ? (
            <TextInput
              //autoCapitalize="none"
              //autoCorrect={false}
              onChangeText={text => searchFilterFunction(text)}
              value={search}
              placeholder="Search users..."
              style={{
                borderRadius: 25,
                borderColor: "#333",
                backgroundColor: "#fff",
                fontSize: 18
              }}
            />
          ) : null
        }
        rightComponent={
          !formVisible ? (
            <Icon
              name="search"
              style={{ marginTop: 6 }}
              size={20}
              onPress={() => showForm()}
            />
          ) : null
        }
        containerStyle={{
          backgroundColor: "#fff",
          //justifyContent: 'space-around',
          height: 100
        }}
      />

      <ScrollView>
        {loading ? (
          <View
            style={{
              justifyContent: "center",
              alignItems: "center",
              marginTop: HEIGHT / 3,
              flex: 1
            }}
          >
            <Image
              source={require("../Images/loader5.gif")}
              style={{ height: 100, width: 100 }}
            />
          </View>
        ) : (
          filteredData.map(item => (
            <RectButton
              onPress={() =>
                navigation.navigate("SingleProfile", {
                  user_id: item.user_id
                })
              }
            >
              <View
                style={{
                  flexDirection: "row",
                  padding: 16,
                  alignItems: "center"
                }}
              >
                <Image
                  source={{ uri: `${url}/code_reservoir/${item.user_img}` }}
                  size="giant"
                  style={styles.userImg}
                />
                <Text
                  style={{
                    color: "#000",
                    marginLeft: 10
                  }}
                >
                  {`${item.full_name}`}
                </Text>
              </View>
            </RectButton>
          ))
        )}
      </ScrollView>
    </View>
  );
};

export default ChatSection;

const styles = StyleSheet.create({
  userImg: {
    height: 50,
    width: 50,
    borderRadius: 50
  },

  container: {
    height: 300,
    margin: 10,
    borderRadius: 6
  }
});
